import React, { useState } from "react";

function TextForm(props) {
  const[text,newText]=useState("Enter some text here.... ")
  const toLowerText=()=>{
    let lText=text.toLowerCase();
    newText(lText)
  }
  const  toUpperText=()=>{
    console.log("uppercase was clicked" +text)
    // newText("New text is updated!!!!!")
    let upText=text.toUpperCase();
    newText(upText)
  }
  const clearText=()=>{
    let cTtex=""
  }
  const handleOnChange=(event)=>{
    console.log("on change")
    newText(event.target.value)
  }
  return (
    <>
    <div className="mb=3">
      <h1 align="center">{props.heading}</h1>
      <textarea
        name="form-label"
        value={text}
        className="form-control"
        id="myBox"
        rows="8"
        onChange={handleOnChange} 
      ></textarea>
      <button className="btn btn-primary my-3" onClick={toUpperText}>Convert to uppercase</button>
      <button className="btn btn-primary mx-4 my-3" onClick={toLowerText}>Convert to Lowercase</button>
      <button className="btn btn-primary mx-2 py-1.5 px-5 my-3"  onClick={clearText}>clear</button>
      {/* <h1>{text}</h1> */}
    </div>
    <div className="container">
      <h1>Your text summmary</h1>
      <p>{text.split(" ").length} Words and {text.length} Characters</p>
    </div>
    </>
  );
}
export default TextForm;
