      <h1 align="center">{props.heading}</h1>
      <textarea
        name="form-label"
        value={text}
        className="form-control"
        id="myBox"
        rows="8"
      ></textarea>
      <button className="btn btn-primary my-3" onClick={toUpperCaseTex}>Convert to uppercase</button>
    </div>
  );
}
export default TextForm;
